/* The following is about making the nested lists look like headings with lists inside */

#questions > ul {
  padding-left: 0px;
}

#questions > ul > li {
  list-style-type: none;
}

#questions > ul > li > a {
  display: block;
  clear: both;
  margin: 1em 0em;
  padding: 0.2em;
  font-size: 125%;
  font-weight: bold;
  text-decoration: none;
  background: rgb(175,175,175);
  color: inherit;
}

#questions > ul > li > ul > li {
  list-style-type: disc;
  font-size: 80%;
  font-weight: bold;
}

